import timeprint('-'*70)print('                         Temperature Converter')def main():        print('-'*70)        time.sleep(2)        userNumber = float(input("Please input the amount of Celcius(Â°C) "+                             "you would like to convert:"))        print('-'*70)        print('Processing...')        userFahrenheit = (userNumber*9/5)+32        userKelvin = userNumber+273.15        print('-'*70)        time.sleep(2)        print('Results:\n')        print('Fahrenheit: {}'.format(userFahrenheit))        print('Kelvin: {}'.format(userKelvin))        print('-'*70)        while True:        main()    time.sleep(2)        if str(input("Would you like to convert again?(Y/N)")).strip().upper() != "Y":              print("\nGoodbye!\n")            time.sleep(1)            break